insert into public.product(name, slug, description, category)
  values ('The JavaScript Ninja', 'javascript-ninja-book', 'What sets "JavaScript Ninja" apart is its emphasis on problem-solving and critical thinking. It encourages you to approach programming challenges creatively, fostering a mindset that goes beyond mere syntax and encourages you to think like a programmer.', 'coding');

insert into public.product(name, slug, description, category)
  values ('Figma Basics Course', 'figma-course', 'Introducing the Figma Basics Course, a comprehensive learning experience designed to teach beginners the essential techniques for creating stunning user interfaces and prototypes using Figma. Master the fundamentals of this powerful design software in no time, and unleash your creativity through hands-on exercises and expert guidance.', 'graphics');

insert into public.product(name, slug, description, category)
  values ('Eloquent JavaScript', 'eloquent-javascript-book', 'Eloquent JavaScript is meticulously crafted to provide a solid foundation in JavaScript programming, enabling you to write elegant and efficient code. Whether you are a novice seeking to grasp the fundamentals or a seasoned developer looking to refine your skills, this book offers a rich learning experience that caters to a wide range of proficiency levels.', 'coding');